.column {
  float: left;
  width: 45%;
  margin-left: 1rem;
}

/* Clear floats after the columns */
.row:after {
  content: "";
  display: table;
  clear: both;
}

#lightgallery > a > img:hover {
   transform: scale(1.2, 1.2);
   transition: 0.4s ease-in-out;
   cursor: pointer;
}

/* Custom class added in functions */
.gallery-link > a {
   color: white
}
.gallery-link > a:hover {
   color: white;
  font-weight: 500;
}


